mapper.select:
SELECT * FROM (SELECT TMP.*, ROWNUM ROW_ID FROM
(SELECT 
FROM XXL_JOB_INFO t


		AND t.job_group = #{jobGroup, jdbcType=NUMERIC}


		AND t.trigger_status = #{triggerStatus, jdbcType=NUMERIC}



		AND t.job_desc like  #{jobDesc_like}



		AND t.executor_handler like #{executorHandler_like}



		AND t.author like #{author_like}


ORDER BY id DESC
) TMP
WHERE ROWNUM  <=  #{pagesize, jdbcType=NUMERIC})
WHERE ROW_ID > #{offset, jdbcType=NUMERIC}

SELECT count(1)
FROM XXL_JOB_INFO t


		AND t.job_group = #{jobGroup, jdbcType=NUMERIC}


		AND t.trigger_status = #{triggerStatus, jdbcType=NUMERIC}



		AND t.job_desc like  #{jobDesc_like}



		AND t.executor_handler like #{executorHandler_like}



		AND t.author like #{author_like}

SELECT 
FROM XXL_JOB_INFO  t
WHERE t.id = #{id, jdbcType=NUMERIC}

SELECT 
FROM XXL_JOB_INFO  t
WHERE t.job_group = #{jobGroup,jdbcType=NUMERIC}

mapper.update:
UPDATE XXL_JOB_INFO
SET
trigger_last_time = #{triggerLastTime,jdbcType=NUMERIC},
trigger_next_time = #{triggerNextTime,jdbcType=NUMERIC},
trigger_status = #{triggerStatus,jdbcType=NUMERIC}
WHERE id = #{id,jdbcType=NUMERIC}
