mapper.select:
SELECT * FROM (SELECT TMP.*, ROWNUM ROW_ID FROM (SELECT FROM XXL_JOB_USER t
AND t.username like CONCAT(CONCAT('%', #{username,jdbcType=VARCHAR}), '%')
AND t.role = #{role,jdbcType=NUMERIC}
ORDER BY username ASC) TMP WHERE ROWNUM <= #{pagesize,jdbcType=NUMERIC}) WHERE ROW_ID > #{offset,jdbcType=NUMERIC}

SELECT count(1) FROM XXL_JOB_USER t
AND t.username like CONCAT(CONCAT('%', #{username,jdbcType=VARCHAR}), '%')
AND t.role = #{role,jdbcType=NUMERIC}

SELECT 
FROM XXL_JOB_USER  t
WHERE t.username = #{username,jdbcType=VARCHAR}

mapper.update:
UPDATE XXL_JOB_USER
SET

	password = #{password,jdbcType=VARCHAR},

role = #{role},
permission = #{permission,jdbcType=VARCHAR}
WHERE id = #{id,jdbcType=NUMERIC}
